def read_file_to_trimmed_array(filename):    elves = []    with open(filename) as file:        elf = []        for line in file:            line = line.rstrip()            if line == "":                elves.append(elf)                elf = []            else:                elf.append(int(line))    elves.append(elf)    return elvesdef find_most_calories(elves):    highest_index = 0    highest_calories = 0    for i in range(1, len(elves) - 1):        calories = sum_items(elves[i])        if calories > highest_calories:            highest_index = i            highest_calories = calories    return highest_index + 1, highest_caloriesdef sum_items(list):    total = 0    for item in list:        total += item    return totalif __name__ == '__main__':    filename = "input.txt"    elves = read_file_to_trimmed_array(filename)    elf_with_most_calories = find_most_calories(elves)    print(f"Elf {elf_with_most_calories} has the most calories")