def read_file_to_array(filename):    pairs = []    with open(filename) as file:        for line in file:            arr = line.rstrip().split(",")            a = arr[0].split("-")            b = arr[1].split("-")            pairs.append([[                int(a[0]), int(a[1])            ], [                int(b[0]), int(b[1])            ]])    return pairsdef get_num_pairs_which_fully_contain_other(pairs):    total = 0    for pair in pairs:        a = pair[0]        b = pair[1]        if a[0] >= b[0] and a[1] <= b[1]:            total += 1        elif b[0] >= a[0] and b[1] <= a[1]:            total += 1    return totaldef test_get_num_pairs_which_partially_contain_other(pairs):    total = 0    for pair in pairs:        a = pair[0]        b = pair[1]        a_range = range(a[0], a[1] + 1)        b_range = range(b[0], b[1] + 1)        if has_overlap(a_range, b_range):            total += 1        # if a[0] >= b[0] and a[1] <= b[1]:        #     total += 1        # elif b[0] >= a[0] and b[1] <= a[1]:        #     total += 1        # elif a[1] >= b[0]:        #     total += 1    return totaldef has_overlap(a_range, b_range):    for i in a_range:        if i in b_range:            return True    for i in b_range:        if i in a_range:            return True    return False